{
    "class": "AS3",
    "action": "deploy",
    "persist": true,
    "syncToGroup": "/Common/sync-failover-dg",
    "declaration": {
        "class": "ADC",
        "schemaVersion": "3.0.0",
        "id": "123456abcde",
        "label": "Basic HTTP",
        "remark": "Basic HTTP Example with monitor",
        "HTTP_LB_stack_1": {
            "class": "Tenant",
            "http_app": {
                "class": "Application",
                "template": "http",
                "serviceMain": {
                    "class": "Service_HTTP",
                    "virtualAddresses": [
                        "10.1.10.51"
                    ],
                    "virtualPort": 80,
                    "pool": "http_pool"
                },
                "http_pool": {
                    "class": "Pool",
                    "monitors": [
                        {
                            "use": "http_get_status"
                        }
                    ],
                    "members": [
                        {
                            "servicePort": 80,
                            "shareNodes": true,
                            "serverAddresses": [
                                "10.1.10.102",
                                "10.1.10.103"
                            ]
                        }
                    ]
                },
                "http_get_status": {
                    "class": "Monitor",
                    "monitorType": "http",
                    "interval": 5,
                    "timeout": 16,
                    "send": "GET / HTTP/1.1\r\nHost: example.com\r\nConnection: close\r\n\r\n",
                    "receive": "^http\/\\d\\.\\d [2-3][0-9][0-9] .*\\r\\n",
                    "adaptive": false
                }
            }
        }
    }
}

