{
    "class": "ADC",
    "schemaVersion": "3.2.0",
    "id": "ltm_policy",
    "label": "",
    "remark": "Simple HTTP application with LTM policy",
    "Sample_http_08": {
        "class": "Tenant",
        "A1": {
            "class": "Application",
            "template": "http",
            "serviceMain": {
                "class": "Service_HTTP",
                "virtualAddresses": [
                    "10.0.1.10"
                ],
                "policyEndpoint": "forward_policy"
            },
            "web_pool": {
                "class": "Pool",
                "monitors": [
                    "http"
                ],
                "members": [
                    {
                        "servicePort": 80,
                        "serverAddresses": [
                            "192.0.2.10",
                            "192.0.2.11"
                        ]
                    }
                ]
            },
            "forward_policy": {
                "class": "Endpoint_Policy",
                "rules": [
                    {
                        "name": "forward_to_pool",
                        "conditions": [
                            {
                                "type": "httpUri",
                                "path": {
