{
    "action": "deploy",
    "class": "AS3",
    "declaration": {
        "{{TENANT}}": {

            "{{APP}}": {
                "{{TCPPROFILE}}": {
                    "class": "TCP_Profile"
                },
                "{{HTTPPROFILE}}": {
                    "class": "HTTP_Profile"
                },
                "{{MONITOR}}": {
                    "class": "Monitor",
                    "interval": {{monitor_interval}},
                    "monitorType": "tcp",
                    "receive": "",
                    "send": "",
                    "timeout": {{monitor_timeout}}
                },
                "{{PERSISTENCE}}": {
                    "class": "Persist",
                    "duration": 1800,
                    "persistenceMethod": "source-address"
                },
                "{{POOL}}": {
                    "class": "Pool",
                    "loadBalancingMode": "round-robin",
                    "members": [
                        {
                            "priorityGroup": 0,
                            "serverAddresses": [
                                "{{pool_member_1_addr}}"
                            ],
                            "servicePort": 80,
                            "shareNodes": true
                        },
                        {
                            "priorityGroup": 0,
                            "serverAddresses": [
                                "{{pool_member_2_addr}}"
                            ],
                            "servicePort": 80,
                            "shareNodes": true
                        }
                    ],
                    "minimumMembersActive": 0,
                    "monitors": [
                        {
                            "use": "{{MONITOR}}"
                        }
                    ],
                    "slowRampTime": 10
                },
				"{{LTM_POLICY}}": {
                    "class": "Endpoint_Policy",
                    "remark": "L7 Policy for application 1",
                    "rules": [
                        {
                            "name": "support_a",
                            "conditions": [
                                {
                                    "type": "httpUri",
                                    "event": "request",
                                    "path": {
                                        "operand": "equals",
                                        "values": [
                                            "/support/mail"
                                        ]
                                    }
                                }
                            ],
                            "actions": [
                                {
                                    "type": "httpRedirect",
                                    "event": "response",
                                    "location": "https://mail.example.com"
                                }
                            ]
                        },
                        {
                            "name": "support_b",
                            "conditions": [
                                {
                                    "type": "httpUri",
                                    "event": "request",
                                    "path": {
                                        "operand": "equals",
                                        "values": [
                                            "/support/www"
                                        ]
                                    }
                                }
                            ],
                            "actions": [
                                {
                                    "type": "httpRedirect",
                                    "event": "response",
                                    "location": "https://www.example.com"
                                }
                            ]
                        }
                    ]
                },
                "class": "Application",
                "{{VS}}": {
	                "metadata": {
						"application_name": {"value":"{{APPLICATION_NAME}}"},
						"application_contact_name": { "value" : "{{APPLICATION_CONTACT_NAME}}" },
						"application_contact_email": {"value" : "{{APPLICATION_CONTACT_EMAIL}}" },
						"application_contact_phone": { "value" : "{{APPLICATION_CONTACT_PHONE}}" },
						"application_description": { "value": "{{APPLICATION_DESCRIPTION}}" }
		                },
                    "class": "Service_HTTP",
                    "layer4": "tcp",
                    "persistenceMethods": [
                        {
                            "use": "{{PERSISTENCE}}"
                        }
                    ],
                    "pool": "{{POOL}}",
                    "profileTCP": {
                        "use": "{{TCPPROFILE}}"
                    },
                    "profileHTTP": {
                        "use": "{{HTTPPROFILE}}"
                    },
                    "virtualAddresses": [
                        "{{http_ltm_policy_vip_address}}"
                    ],
                    "virtualPort": 80,
					"policyEndpoint": "{{LTM_POLICY}}"
                },
                "template": "generic"
            },
            "class": "Tenant"
        },
        "class": "ADC",
        "id": "{{APP}}",
        "label": "{{APP}}",
        "remark": "{{APPLICATION_NAME}} | {{APPLICATION_CONTACT_NAME}}",
        "schemaVersion": "3.7.0",
        "target": {
            "address": "{{bip_ip_address}}"
        }
    },
    "persist": true
}

